#include <iostream>
#include <string>
#include <vector>

#include "NormalData.h"

using namespace std;

class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        vector<int> countS(26), countP(26);
        int szP = p.size(), szS = s.size();

        for (auto a: p) {
            countP[a - 'a']++;
        }

        for (int i = 0; i < szP; ++i) {
            countS[s[i] - 'a']++;
        }

        vector<int> res;

        if (countS == countP) {
            res.push_back(0);
        }

        // be careful about the boundary not szS - szP + 1
        for (int i = szP; i < szS; ++i) {
            countS[s[i - szP] - 'a']--;
            countS[s[i] - 'a']++;

            if (countS == countP) {
                // be careful about the index
                res.push_back(i - szP + 1);
            }
        }

        return res;
    }

    vector<int> findAnagrams1(string s, string p) {
        int szS = s.size(), szP = p.size();
        szS = szS - szP + 1;

        vector<int> res;
        sort(p.begin(), p.end());

        for (int i = 0; i < szS; ++i) {
            string tmp = s.substr(i, szP);
            sort(tmp.begin(), tmp.end());
            
            if (tmp == p) {
                res.push_back(i);
            }
        }

        return res;
    }
};

int main()
{
    Solution sln;
    string s("cbaebabacd"), p("abc");
    //string s("abab"), p("ab");
//string s(""), p("");
    vector<int> res = sln.findAnagrams(s, p);
    printVector<int>(res);
    return 0;
}
